// ===== SYNTH FUNCTIONS =====
function kick(t, x, d) {
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'sine';
  o.frequency.setValueAtTime(150, t);
  o.frequency.exponentialRampToValueAtTime(30, t + 0.12);
  g.gain.setValueAtTime(0.6, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.2);
  o.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + 0.25);
}

function snare(t, x, d) {
  const b = x.sampleRate * 0.08,
    bf = x.createBuffer(1, b, x.sampleRate),
    dt = bf.getChannelData(0);
  for (let i = 0; i < b; i++) dt[i] = (Math.random() * 2 - 1) * 0.7;
  const n = x.createBufferSource();
  n.buffer = bf;
  const g = x.createGain(), f = x.createBiquadFilter();
  f.type = 'highpass';
  f.frequency.value = 1500;
  g.gain.setValueAtTime(0.4, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.1);
  n.connect(f);
  f.connect(g);
  g.connect(d);
  n.start(t);
  n.stop(t + 0.12);
  const o = x.createOscillator(), g2 = x.createGain();
  o.type = 'triangle';
  o.frequency.setValueAtTime(180, t);
  o.frequency.exponentialRampToValueAtTime(80, t + 0.05);
  g2.gain.setValueAtTime(0.3, t);
  g2.gain.exponentialRampToValueAtTime(0.001, t + 0.07);
  o.connect(g2);
  g2.connect(d);
  o.start(t);
  o.stop(t + 0.1);
}

function hihat(t, x, d) {
  const b = x.sampleRate * 0.03,
    bf = x.createBuffer(1, b, x.sampleRate),
    dt = bf.getChannelData(0);
  for (let i = 0; i < b; i++) dt[i] = Math.random() * 2 - 1;
  const n = x.createBufferSource();
  n.buffer = bf;
  const g = x.createGain(), f = x.createBiquadFilter();
  f.type = 'highpass';
  f.frequency.value = 7000;
  g.gain.setValueAtTime(0.18, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.04);
  n.connect(f);
  f.connect(g);
  g.connect(d);
  n.start(t);
  n.stop(t + 0.05);
}

function shaker(t, x, d) {
  const b = x.sampleRate * 0.05,
    bf = x.createBuffer(1, b, x.sampleRate),
    dt = bf.getChannelData(0);
  for (let i = 0; i < b; i++) dt[i] = (Math.random() * 2 - 1) * 0.4;
  const n = x.createBufferSource();
  n.buffer = bf;
  const g = x.createGain(), f = x.createBiquadFilter();
  f.type = 'bandpass';
  f.frequency.value = 5000;
  f.Q.value = 2;
  g.gain.setValueAtTime(0.12, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.05);
  n.connect(f);
  f.connect(g);
  g.connect(d);
  n.start(t);
  n.stop(t + 0.07);
}

function elec(t, x, d) {
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'square';
  o.frequency.setValueAtTime(800, t);
  o.frequency.exponentialRampToValueAtTime(100, t + 0.05);
  g.gain.setValueAtTime(0.15, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.07);
  o.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + 0.09);
}

function lead(t, x, d, f, w, v) {
  if (!f) return;
  const o = x.createOscillator(), g = x.createGain(), fl = x.createBiquadFilter();
  o.type = w;
  o.frequency.value = f;
  fl.type = 'lowpass';
  fl.frequency.value = f * 3;
  g.gain.setValueAtTime(v, t);
  g.gain.setValueAtTime(v, t + ST * 0.7);
  g.gain.exponentialRampToValueAtTime(0.001, t + ST * 0.95);
  o.connect(fl);
  fl.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + ST);
}

function vibes(t, x, d, f) {
  if (!f) return;
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'sine';
  o.frequency.value = f;
  const tr = x.createOscillator(), tg = x.createGain();
  tr.type = 'sine';
  tr.frequency.value = 6;
  tg.gain.value = 0.04;
  tr.connect(tg);
  tg.connect(g.gain);
  g.gain.setValueAtTime(0.18, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + ST * 3);
  o.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + ST * 3);
  tr.start(t);
  tr.stop(t + ST * 3);
}

function pad(t, x, d, fs) {
  const dur = ST * STEPS;
  fs.forEach(f => {
    const o = x.createOscillator(), g = x.createGain();
    o.type = 'sine';
    o.frequency.value = f;
    g.gain.setValueAtTime(0, t);
    g.gain.linearRampToValueAtTime(0.05, t + 0.4);
    g.gain.setValueAtTime(0.05, t + dur - 0.4);
    g.gain.linearRampToValueAtTime(0, t + dur);
    o.connect(g);
    g.connect(d);
    o.start(t);
    o.stop(t + dur + 0.1);
    const o2 = x.createOscillator(), g2 = x.createGain();
    o2.type = 'sine';
    o2.frequency.value = f * 1.003;
    g2.gain.setValueAtTime(0, t);
    g2.gain.linearRampToValueAtTime(0.03, t + 0.4);
    g2.gain.setValueAtTime(0.03, t + dur - 0.4);
    g2.gain.linearRampToValueAtTime(0, t + dur);
    o2.connect(g2);
    g2.connect(d);
    o2.start(t);
    o2.stop(t + dur + 0.1);
  });
}

function sub(t, x, d, f) {
  if (!f) return;
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'sine';
  o.frequency.value = f;
  g.gain.setValueAtTime(0.2, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + ST * 4);
  o.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + ST * 4);
}

function vocal(t, x, d, f, tp) {
  if (!f) return;
  const o = x.createOscillator(), g = x.createGain(), fl = x.createBiquadFilter();
  if (tp === 'high') {
    o.type = 'triangle';
    fl.type = 'bandpass';
    fl.frequency.value = f;
    fl.Q.value = 3;
    g.gain.setValueAtTime(0.13, t);
  } else if (tp === 'low') {
    o.type = 'sawtooth';
    fl.type = 'lowpass';
    fl.frequency.value = f * 2;
    g.gain.setValueAtTime(0.1, t);
  } else {
    o.type = 'triangle';
    fl.type = 'bandpass';
    fl.frequency.value = f;
    fl.Q.value = 2;
    g.gain.setValueAtTime(0.11, t);
  }
  o.frequency.value = f;
  const v = x.createOscillator(), vg = x.createGain();
  v.type = 'sine';
  v.frequency.value = 5.5;
  vg.gain.value = f * 0.015;
  v.connect(vg);
  vg.connect(o.frequency);
  g.gain.exponentialRampToValueAtTime(0.001, t + ST * 2);
  o.connect(fl);
  fl.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + ST * 2.5);
  v.start(t);
  v.stop(t + ST * 2.5);
}

function darkVocal(t, x, d, f) {
  const dur = ST * STEPS;
  const o = x.createOscillator(), o2 = x.createOscillator(), g = x.createGain();
  o.type = 'sawtooth';
  o.frequency.value = f;
  o2.type = 'sawtooth';
  o2.frequency.value = f * 1.01;
  g.gain.setValueAtTime(0, t);
  g.gain.linearRampToValueAtTime(0.06, t + 0.8);
  g.gain.setValueAtTime(0.06, t + dur - 0.8);
  g.gain.linearRampToValueAtTime(0, t + dur);
  const fl = x.createBiquadFilter();
  fl.type = 'lowpass';
  fl.frequency.value = 200;
  o.connect(fl);
  o2.connect(fl);
  fl.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + dur + 0.1);
  o2.start(t);
  o2.stop(t + dur + 0.1);
}

function darkVocal2(t, x, d, f) {
  if (!f) return;
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'sawtooth';
  o.frequency.value = f;
  const fl = x.createBiquadFilter();
  fl.type = 'lowpass';
  fl.frequency.value = 300;
  g.gain.setValueAtTime(0.08, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + ST * 3);
  o.connect(fl);
  fl.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + ST * 3);
}

function glitch(t, x, d) {
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'square';
  o.frequency.setValueAtTime(200 + Math.random() * 2000, t);
  o.frequency.setValueAtTime(100 + Math.random() * 3000, t + 0.02);
  g.gain.setValueAtTime(0.07, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.05);
  o.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + 0.07);
}

function whistle(t, x, d, f) {
  if (!f) return;
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'sine';
  o.frequency.value = f;
  g.gain.setValueAtTime(0, t);
  g.gain.linearRampToValueAtTime(0.09, t + 0.04);
  g.gain.setValueAtTime(0.09, t + ST * 1.5);
  g.gain.exponentialRampToValueAtTime(0.001, t + ST * 3);
  o.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + ST * 3);
}

function robot(t, x, d, f) {
  if (!f) return;
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'square';
  o.frequency.value = f;
  const fl = x.createBiquadFilter();
  fl.type = 'bandpass';
  fl.frequency.value = f;
  fl.Q.value = 5;
  g.gain.setValueAtTime(0.08, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + ST * 1.5);
  o.connect(fl);
  fl.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + ST * 2);
}

function sonicKick(t, x, d) {
  const o = x.createOscillator(), g = x.createGain();
  o.type = 'sine';
  o.frequency.setValueAtTime(200, t);
  o.frequency.exponentialRampToValueAtTime(40, t + 0.08);
  g.gain.setValueAtTime(0.5, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.15);
  o.connect(g);
  g.connect(d);
  o.start(t);
  o.stop(t + 0.2);
}

function powerHit(t, x, d) {
  const b = x.sampleRate * 0.12,
    bf = x.createBuffer(1, b, x.sampleRate),
    dt = bf.getChannelData(0);
  for (let i = 0; i < b; i++) dt[i] = Math.random() * 2 - 1;
  const n = x.createBufferSource();
  n.buffer = bf;
  const g = x.createGain();
  g.gain.setValueAtTime(0.5, t);
  g.gain.exponentialRampToValueAtTime(0.001, t + 0.15);
  n.connect(g);
  g.connect(d);
  n.start(t);
  n.stop(t + 0.18);
  const o = x.createOscillator(), g2 = x.createGain();
  o.type = 'sine';
  o.frequency.setValueAtTime(100, t);
  o.frequency.exponentialRampToValueAtTime(30, t + 0.1);
  g2.gain.setValueAtTime(0.6, t);
  g2.gain.exponentialRampToValueAtTime(0.001, t + 0.15);
  o.connect(g2);
  g2.connect(d);
  o.start(t);
  o.stop(t + 0.2);
}

// ===== SVG CHARACTER BUILDERS =====
function sprunkiSVG(main, dark, feature) {
  const eyeW = 10, eyeH = 12;
  let featureSVG = '';
  switch (feature) {
    case 'antennas':
      featureSVG = `<line x1="28" y1="18" x2="24" y2="4" stroke="${dark}" stroke-width="3" stroke-linecap="round"/> <line x1="44" y1="18" x2="48" y2="4" stroke="${dark}" stroke-width="3" stroke-linecap="round"/> <circle cx="24" cy="3" r="3" fill="${main}"/><circle cx="48" cy="3" r="3" fill="${main}"/>`;
      break;
    case 'horns':
      featureSVG = `<polygon points="22,18 18,2 26,14" fill="${dark}"/> <polygon points="30,16 28,0 34,12" fill="${dark}"/> <polygon points="36,14 36,0 42,12" fill="${dark}"/> <polygon points="42,16 44,0 48,14" fill="${dark}"/> <polygon points="50,18 54,2 46,14" fill="${dark}"/>`;
