      break;
    case 'dragon':
      featureSVG = `<polygon points="24,20 20,2 28,16" fill="${dark}"/> <polygon points="48,20 52,2 44,16" fill="${dark}"/> <ellipse cx="20" cy="32" rx="8" ry="5" fill="${dark}" opacity="0.6"/> <ellipse cx="52" cy="32" rx="8" ry="5" fill="${dark}" opacity="0.6"/>`;
      break;
    case 'bunny':
      featureSVG = `<ellipse cx="26" cy="10" rx="5" ry="14" fill="${main}" stroke="${dark}" stroke-width="1"/> <ellipse cx="46" cy="10" rx="5" ry="14" fill="${main}" stroke="${dark}" stroke-width="1"/> <ellipse cx="26" cy="8" rx="3" ry="10" fill="${dark}" opacity="0.3"/> <ellipse cx="46" cy="8" rx="3" ry="10" fill="${dark}" opacity="0.3"/> <circle cx="38" cy="18" r="3" fill="#FF3366"/>`;
      break;
    case 'horn1':
      featureSVG = `<polygon points="36,18 32,0 40,0" fill="${dark}"/>`;
      break;
    case 'crown':
      featureSVG = `<polygon points="24,18 20,6 28,12 36,2 44,12 52,6 48,18" fill="${dark}" opacity="0.7"/>`;
      break;
    case 'cat':
      featureSVG = `<polygon points="20,24 16,6 30,20" fill="${main}" stroke="${dark}" stroke-width="1"/> <polygon points="52,24 56,6 42,20" fill="${main}" stroke="${dark}" stroke-width="1"/>`;
      break;
    case 'ears':
      featureSVG = `<ellipse cx="22" cy="16" rx="10" ry="8" fill="${main}"/> <ellipse cx="50" cy="16" rx="10" ry="8" fill="${main}"/>`;
      break;
    case 'spikes':
      featureSVG = `<polygon points="26,18 22,4 30,14" fill="${dark}"/> <polygon points="36,16 36,0 40,12" fill="${dark}"/> <polygon points="46,18 50,4 42,14" fill="${dark}"/>`;
      break;
    case 'robot':
      featureSVG = `<rect x="26" y="8" width="20" height="8" rx="2" fill="${dark}"/> <rect x="30" y="4" width="12" height="6" rx="2" fill="${main}" opacity="0.6"/>`;
      break;
    case 'tree':
      featureSVG = `<ellipse cx="36" cy="8" rx="16" ry="12" fill="#2D6A2F"/> <ellipse cx="30" cy="6" rx="10" ry="8" fill="#3A7A3A"/> <ellipse cx="42" cy="6" rx="10" ry="8" fill="#3A7A3A"/>`;
      break;
    case 'sun':
      featureSVG = `<circle cx="36" cy="8" r="10" fill="${main}"/> <line x1="36" y1="-4" x2="36" y2="0" stroke="${dark}" stroke-width="2"/> <line x1="46" y1="0" x2="48" y2="-2" stroke="${dark}" stroke-width="2"/> <line x1="26" y1="0" x2="24" y2="-2" stroke="${dark}" stroke-width="2"/>`;
      break;
    default:
      featureSVG = `<circle cx="36" cy="12" r="5" fill="${dark}"/>`;
  }
  return `<svg viewBox="0 0 72 110" xmlns="http://www.w3.org/2000/svg">
    ${featureSVG}
    <ellipse cx="36" cy="62" rx="26" ry="36" fill="${main}"/>
    <ellipse cx="36" cy="62" rx="24" ry="34" fill="${main}" stroke="${dark}" stroke-width="1.5"/>
    <ellipse cx="36" cy="100" rx="16" ry="6" fill="${dark}" opacity="0.3"/>
    <ellipse cx="28" cy="52" rx="${eyeW / 2}" ry="${eyeH / 2}" fill="#fff"/>
    <ellipse cx="44" cy="52" rx="${eyeW / 2}" ry="${eyeH / 2}" fill="#fff"/>
    <circle cx="28" cy="54" r="3.5" fill="#111"/>
    <circle cx="44" cy="54" r="3.5" fill="#111"/>
    <circle cx="29" cy="52" r="1.5" fill="#fff"/>
    <circle cx="45" cy="52" r="1.5" fill="#fff"/>
    <ellipse cx="36" cy="62" rx="5" ry="3" fill="${dark}" opacity="0.4"/>
    <ellipse cx="24" cy="70" rx="8" ry="4" fill="${dark}" opacity="0.2"/>
    <ellipse cx="48" cy="70" rx="8" ry="4" fill="${dark}" opacity="0.2"/>
  </svg>`;
}

function sonicSVG(main, dark, character) {
  let extra = '';
  switch (character) {
    case 'sonic':
      extra = `<polygon points="44,20 60,8 48,24" fill="${main}"/> <polygon points="48,16 66,12 52,22" fill="${main}"/> <polygon points="42,14 54,4 46,20" fill="${main}"/> <ellipse cx="36" cy="90" rx="8" ry="5" fill="#FF0000"/> <rect x="32" y="92" width="8" height="3" rx="1" fill="#FF0000"/>`;
      break;
    case 'tails':
      extra = `<ellipse cx="20" cy="8" rx="5" ry="10" fill="${main}" transform="rotate(-20,20,8)"/> <ellipse cx="50" cy="12" rx="5" ry="10" fill="${main}" transform="rotate(20,50,12)"/> <ellipse cx="16" cy="88" rx="12" ry="6" fill="${main}" transform="rotate(-15,16,88)"/> <ellipse cx="10" cy="84" rx="12" ry="6" fill="${main}" transform="rotate(-30,10,84)"/> <circle cx="18" cy="92" r="3" fill="#fff"/>`;
      break;
    case 'knuckles':
      extra = `<polygon points="22,24 14,10 30,20" fill="${main}"/> <polygon points="50,24 58,10 42,20" fill="${main}"/> <ellipse cx="16" cy="78" rx="12" ry="7" fill="#fff"/> <ellipse cx="56" cy="78" rx="12" ry="7" fill="#fff"/> <circle cx="14" cy="76" r="3" fill="${main}"/> <circle cx="18" cy="78" r="3" fill="${main}"/> <circle cx="54" cy="76" r="3" fill="${main}"/> <circle cx="58" cy="78" r="3" fill="${main}"/>`;
      break;
    case 'eggman':
      extra = `<ellipse cx="36" cy="62" rx="30" ry="38" fill="${main}"/> <rect x="22" y="58" width="28" height="4" rx="2" fill="#A05020"/> <circle cx="36" cy="26" r="14" fill="#FFD0A0"/> <rect x="20" y="38" width="32" height="6" rx="3" fill="#A05020"/>`;
      break;
    case 'shadow':
      extra = `<polygon points="44,20 62,6 48,24" fill="${main}"/> <polygon points="48,16 68,10 52,22" fill="${main}"/> <polygon points="42,14 56,2 46,20" fill="${main}"/> <line x1="46" y1="22" x2="62" y2="8" stroke="#AA0000" stroke-width="3"/> <line x1="50" y1="18" x2="66" y2="12" stroke="#AA0000" stroke-width="3"/> <ellipse cx="36" cy="90" rx="8" ry="5" fill="#555"/> <rect x="30" y="85" width="12" height="3" rx="1" fill="#AA0000"/>`;
      break;
    case 'jet':
      extra = `<polygon points="36,24 30,14 42,14" fill="${main}"/> <polygon points="36,18 28,8 44,8" fill="${dark}"/> <polygon points="36,56 28,62 36,64" fill="#FFA000"/> <ellipse cx="36" cy="20" rx="16" ry="4" fill="rgba(255,255,255,0.3)"/>`;
      break;
  }
  const isEgg = character === 'eggman';
  const bodyRX = isEgg ? 30 : 24;
  const bodyRY = isEgg ? 38 : 34;
  return `<svg viewBox="0 0 72 110" xmlns="http://www.w3.org/2000/svg">
    ${extra}
    ${!isEgg ? `<ellipse cx="36" cy="62" rx="${bodyRX}" ry="${bodyRY}" fill="${main}" stroke="${dark}" stroke-width="1.5"/>` : ''}
    <ellipse cx="36" cy="100" rx="14" ry="5" fill="rgba(0,0,0,0.2)"/>
    ${!isEgg ? `
      <ellipse cx="30" cy="50" rx="6" ry="7" fill="#fff"/>
      <ellipse cx="42" cy="50" rx="6" ry="7" fill="#fff"/>
      <circle cx="31" cy="52" r="3" fill="#111"/>
      <circle cx="43" cy="52" r="3" fill="#111"/>
      <circle cx="32" cy="50" r="1.2" fill="#fff"/>
      <circle cx="44" cy="50" r="1.2" fill="#fff"/>
    ` : `
      <circle cx="30" cy="30" r="4" fill="#fff"/>
      <circle cx="42" cy="30" r="4" fill="#fff"/>
      <circle cx="31" cy="31" r="2" fill="#111"/>
      <circle cx="43" cy="31" r="2" fill="#111"/>
    `}
    <ellipse cx="36" cy="${isEgg ? 68 : 60}" rx="4" ry="2" fill="${dark}" opacity="0.4"/>
  </svg>`;
}

// ===== SEQUENCER =====
let ctx = null, master = null, step = 0, nextT = 0, timer = null, playing = false;
let phase = 0, actives = new Set(), pChars = [];
let phasesCompleted = [false, false, false, false, false];

function initA() {
  if (!ctx) {
    ctx = new (window.AudioContext || window.webkitAudioContext)();
    master = ctx.createGain();
    master.gain.value = 0.8;
    master.connect(ctx.destination);
  }
  if (ctx.state === 'suspended') ctx.resume();
}

function startSeq() {
  if (playing) return;
  playing = true;
  step = 0;
  nextT = ctx.currentTime + 0.05;
  sched();
}

function stopSeq() {
  playing = false;
  if (timer) {
    clearTimeout(timer);
    timer = null;
  }
}

function sched() {
  if (!playing) return;
  while (nextT < ctx.currentTime + 0.15) {
    schedStep(step, nextT);
    step = (step + 1) % STEPS;
    nextT += ST;
  }
  timer = setTimeout(sched, 50);
}

function schedStep(s, t) {
  setTimeout(() => {
    updStepDots(s);
    updViz(s);
  }, (t - ctx.currentTime) * 1000);
  actives.forEach(ci => {
    const ch = pChars[ci];
    if (!ch || !ch.p[s]) return;
    const nt = ch.n ? ch.n[s] : null;
    ch.snd(t, ctx, master, nt);
  });
}

// ===== GAME LOGIC =====
function getPC(p) {
  return CHARS.slice(p * 5, (p === 4 ? p * 5 + 6 : p * 5 + 5));
}

function toggle(i) {
  initA();
  if (actives.has(i)) {
    actives.delete(i);
    if (actives.size === 0) stopSeq();
  } else {
    actives.add(i);
    if (!playing) startSeq();
    particles(pChars[i].c, i);
  }
  render();
  if (actives.size === pChars.length) {
    phasesCompleted[phase] = true;
    setTimeout(() => {
      if (actives.size === pChars.length) checkWin();
    }, ST * STEPS * 2 * 1000);
  }
}

function changePhase(dir) {
  const np = phase + dir;
  if (np < 0 || np >= PHASES.length) return;
  if (np > 0 && !phasesCompleted[np - 1] && np !== phase) return;
  stopSeq();
  actives.clear();
  phase = np;
  pChars = getPC(phase);
  applyPhaseVisuals();
  render();
}

function checkWin() {
  if (phase >= PHASES.length - 1) {
    document.getElementById('win').classList.add('show');
    stopSeq();
    return;
  }
  const ov = document.getElementById('overlay'),
    ot = document.getElementById('overlay-text'),
    os = document.getElementById('overlay-sub');
  ov.classList.add('show');
  const np = phase + 1;
  ot.textContent = PHASES[np].name;
  os.textContent = PHASES[np].sub;
  setTimeout(() => {
    stopSeq();
    actives.clear();
    phase = np;
    pChars = getPC(phase);
    applyPhaseVisuals();
    render();
    setTimeout(() => ov.classList.remove('show'), 500);
  }, 1500);
}

function restart() {
  stopSeq();
  phase = 0;
  actives.clear();
  phasesCompleted = [false, false, false, false, false];
  document.getElementById('win').classList.remove('show');
  pChars = getPC(0);
  applyPhaseVisuals();
  render();
}

// ===== VISUALS =====
function applyPhaseVisuals() {
  const ph = PHASES[phase];
  document.getElementById('sky').style.background = `linear-gradient(180deg,${ph.sky[0]} 0%,${ph.sky[1]} 40%,${ph.sky[2]} 100%)`;
  document.getElementById('grass-line').style.background = ph.grass;
  document.getElementById('checkerboard').style.backgroundImage = `repeating-conic-gradient(${ph.check1} 0% 25%, ${ph.check2} 0% 50%)`;
  document.querySelectorAll('.star').forEach(s => s.style.opacity = ph.stars ? '1' : '0');
  document.getElementById('hud-phase').textContent = `FASE ${phase + 1}`;
  document.getElementById('phase-label').textContent = ph.name;
  if (phase >= 3)
    document.getElementById('scene').style.filter = 'saturate(0.4) contrast(1.2) brightness(0.8)';
  else if (phase >= 2)
    document.getElementById('scene').style.filter = 'saturate(0.7) contrast(1.1)';
  else
    document.getElementById('scene').style.filter = 'none';
  document.getElementById('nav-left').classList.toggle('disabled', phase === 0);
  document.getElementById('nav-right').classList.toggle('disabled', phase >= PHASES.length - 1 || !phasesCompleted[phase]);
}

function render() {
  const stage = document.getElementById('stage');
  stage.innerHTML = '';
  pChars = getPC(phase);
  pChars.forEach((ch, i) => {
    const on = actives.has(i);
    const el = document.createElement('div');
    el.className = `chr ${on ? 'on' : 'off'}`;
    el.style.setProperty('--c', ch.c);
    el.onclick = () => toggle(i);
    el.innerHTML = `
      <div class="chr-svg" style="position:relative">${ch.svg()}<div class="chr-note">♪</div></div>
      <div class="chr-name">${ch.name}</div>
      <div class="chr-type">${ch.type}</div>
    `;
    stage.appendChild(el);
  });
  document.getElementById('hud-score').textContent = `${actives.size}/${pChars.length}`;
  const pct = (actives.size / pChars.length) * 100;
  document.getElementById('prog-bar').style.width = pct + '%';
  document.getElementById('prog-text').textContent = actives.size === pChars.length ? 'COMPLETO!' : 'Ative todos para completar';
  document.getElementById('nav-left').classList.toggle('disabled', phase === 0);
  document.getElementById('nav-right').classList.toggle('disabled', phase >= PHASES.length - 1 || !phasesCompleted[phase]);
}

function initDots() {
  const el = document.getElementById('step-row');
  el.innerHTML = '';
  for (let i = 0; i < STEPS; i++) {
    const d = document.createElement('div');
    d.className = 'sdot';
    el.appendChild(d);
  }
}

function updStepDots(s) {
  document.querySelectorAll('.sdot').forEach((d, i) => d.classList.toggle('on', i === s));
}

function initViz() {
  const el = document.getElementById('viz');
  el.innerHTML = '';
  for (let i = 0; i < 28; i++) {
    const b = document.createElement('div');
    b.className = 'vbar';
    el.appendChild(b);
  }
}

function updViz(s) {
  if (actives.size === 0) return;
  const bars = document.querySelectorAll('.vbar');
  const int = actives.size / pChars.length;
  const beat = s % 4 === 0;
  bars.forEach((b, i) => {
    b.style.height = (2 + Math.random() * 25 * int * (beat ? 1.5 : 0.6)) + 'px';
    b.style.background = `hsl(${(40 + i * 3) % 60},70%,${50 - phase * 5}%)`;
  });
}

setInterval(() => {
  if (actives.size === 0)
    document.querySelectorAll('.vbar').forEach(b => {
      b.style.height = '2px';
      b.style.background = 'rgba(201,168,76,0.1)';
    });
}, 300);

function particles(c, i) {
  const el = document.querySelectorAll('.chr')[i];
  if (!el) return;
  const r = el.getBoundingClientRect();
  for (let j = 0; j < 5; j++) {
    const p = document.createElement('div');
    p.className = 'ptcl';
    p.style.background = c;
    p.style.left = (r.left + r.width / 2 + (Math.random() - 0.5) * 40) + 'px';
    p.style.top = (r.top + (Math.random() - 0.5) * 20) + 'px';
    p.style.width = p.style.height = (2 + Math.random() * 4) + 'px';
    document.body.appendChild(p);
    setTimeout(() => p.remove(), 1500);
  }
}

function buildBG() {
  const mt = document.getElementById('mountains');
  for (let i = 0; i < 12; i++) {
    const m = document.createElement('div');
    m.className = 'mountain';
    m.style.left = (i * 160) + 'px';
    m.style.borderBottomWidth = (60 + Math.random() * 80) + 'px';
    m.style.borderLeftWidth = (50 + Math.random() * 60) + 'px';
    m.style.borderRightWidth = (50 + Math.random() * 60) + 'px';
    mt.appendChild(m);
  }
  const hl = document.getElementById('hills');
  for (let i = 0; i < 16; i++) {
    const h = document.createElement('div');
    h.className = 'hill';
    h.style.left = (i * 120) + 'px';
    h.style.width = (80 + Math.random() * 80) + 'px';
    h.style.height = (30 + Math.random() * 40) + 'px';
    hl.appendChild(h);
  }
  const sky = document.getElementById('sky');
  for (let i = 0; i < 40; i++) {
    const s = document.createElement('div');
    s.className = 'star';
    s.style.left = Math.random() * 100 + '%';
    s.style.top = Math.random() * 60 + '%';
    s.style.width = s.style.height = (1 + Math.random() * 2) + 'px';
    sky.appendChild(s);
  }
}

document.addEventListener('keydown', e => {
  if (e.code === 'ArrowLeft') {
    e.preventDefault();
    changePhase(-1);
  } else if (e.code === 'ArrowRight') {
    e.preventDefault();
    changePhase(1);
  } else if (e.code === 'Space') {
    e.preventDefault();
  } else if (e.key >= '1' && e.key <= '6') {
    const i = parseInt(e.key) - 1;
    if (i < pChars.length) toggle(i);
  }
});

buildBG();
initDots();
initViz();
pChars = getPC(0);
applyPhaseVisuals();
render();
```

This represents the complete game code: five phases with themed characters, 25 characters total, full synth-based sound generation, sequencer logic, visual transitions, and interactive gameplay mechanics.

