// ============================================================
// SPRUNKS — Sistema de Tipos
// Sequenciador musical com personagens customizáveis
// ============================================================

/** Frequências musicais em Hz — escala Cm */
export interface NoteMap {
  [key: string]: number
}

/** Tipo de som do personagem */
export type SoundType = 'BEAT' | 'MELODIA' | 'VOCAL' | 'EFEITO'

/** Função de síntese — recebe tempo, contexto de áudio, destino e frequência opcional */
export type SynthFunction = (
  time: number,
  ctx: AudioContext,
  dest: AudioNode,
  freq?: number
) => void

/** Função que gera SVG do personagem */
export type SVGGenerator = () => string

/** Um personagem do jogo */
export interface Character {
  name: string
  color: string
  type: SoundType
  description: string
  /** Pattern de 16 steps — 1 = toca, 0 = silêncio */
  pattern: number[]
  /** Notas (frequências) por step — 0 = sem nota */
  notes?: number[]
  /** Função de síntese do som */
  synth: SynthFunction
  /** SVG inline do personagem */
  svg: SVGGenerator
  /** Se é personagem surpresa (gerado por prompt dos pais) */
  isSurprise?: boolean
  /** Tema do personagem (ex: "sonic", "mario", "pikachu") */
  theme?: string
}

/** Configuração visual de uma fase */
export interface Phase {
  name: string
  subtitle: string
  /** Gradiente do céu — 3 cores [topo, meio, base] */
  skyGradient: [string, string, string]
  grassColor: string
  checkerColor1: string
  checkerColor2: string
  showStars: boolean
  /** Se é fase surpresa */
  isSurprise?: boolean
  /** Tema da fase surpresa */
  surpriseTheme?: string
}

/** Preferência da criança — preenchida pelos pais */
export interface ChildPreference {
  /** Nome da criança */
  childName: string
  /** Personagem/tema favorito (ex: "Sonic", "Mario", "Dragon Ball") */
  favoriteTheme: string
  /** Cores favoritas */
  favoriteColors: string[]
  /** Descrição livre dos pais sobre o que a criança gosta */
  parentDescription: string
}

/** Personagem gerado pela API baseado no prompt dos pais */
export interface GeneratedCharacter {
  name: string
  primaryColor: string
  secondaryColor: string
  features: string[] // elementos visuais do SVG
  soundProfile: SoundType
  theme: string
}

/** Estado do sequenciador */
export interface SequencerState {
  playing: boolean
  currentStep: number
  bpm: number
  totalSteps: number
}

/** Estado geral do jogo */
export interface GameState {
  currentPhase: number
  activeCharacters: Set<number>
  phasesCompleted: boolean[]
  totalPhases: number
  surpriseInterval: number // a cada quantas fases aparece surpresa (3 ou 4)
  childPreference: ChildPreference | null
}

