// ============================================================
// SPRUNKS — Singleton do AudioContext
// O "palco de som" — criado uma vez, usado por todos
// ============================================================

let audioCtx: AudioContext | null = null
let masterGain: GainNode | null = null

/**
 * Inicializa o AudioContext (o "estúdio de gravação" do browser).
 * IMPORTANTE: precisa ser chamado após interação do usuário (clique/toque)
 * porque browsers bloqueiam áudio automático.
 */
export function initAudio(): { ctx: AudioContext; master: GainNode } {
  if (!audioCtx) {
    audioCtx = new (window.AudioContext || (window as any).webkitAudioContext)()
    masterGain = audioCtx.createGain()
    masterGain.gain.value = 0.8
    masterGain.connect(audioCtx.destination)
  }

  // Se tava pausado (política do browser), retoma
  if (audioCtx.state === 'suspended') {
    audioCtx.resume()
  }

  return { ctx: audioCtx, master: masterGain! }
}

/** Retorna o AudioContext atual (pode ser null se não inicializado) */
export function getAudioContext(): AudioContext | null {
  return audioCtx
}

/** Retorna o GainNode master (volume geral) */
export function getMasterGain(): GainNode | null {
  return masterGain
}

/** Ajusta volume geral (0 a 1) */
export function setMasterVolume(volume: number): void {
  if (masterGain) {
    masterGain.gain.value = Math.max(0, Math.min(1, volume))
  }
}

/** Destrói o AudioContext — usar ao desmontar o componente */
export function destroyAudio(): void {
  if (audioCtx) {
    audioCtx.close()
    audioCtx = null
    masterGain = null
  }
}

