  osc.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + 0.09)
}

/** Bumbo rápido estilo Sonic */
export function sonicKick(t: number, ctx: AudioContext, dest: AudioNode): void {
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  osc.type = 'sine'
  osc.frequency.setValueAtTime(200, t)
  osc.frequency.exponentialRampToValueAtTime(40, t + 0.08)
  gain.gain.setValueAtTime(0.5, t)
  gain.gain.exponentialRampToValueAtTime(0.001, t + 0.15)
  osc.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + 0.2)
}

/** Pancada forte — ruído + grave */
export function powerHit(t: number, ctx: AudioContext, dest: AudioNode): void {
  const bufSize = ctx.sampleRate * 0.12
  const buf = ctx.createBuffer(1, bufSize, ctx.sampleRate)
  const data = buf.getChannelData(0)
  for (let i = 0; i < bufSize; i++) data[i] = Math.random() * 2 - 1
  const noise = ctx.createBufferSource()
  noise.buffer = buf
  const gain = ctx.createGain()
  gain.gain.setValueAtTime(0.5, t)
  gain.gain.exponentialRampToValueAtTime(0.001, t + 0.15)
  noise.connect(gain)
  gain.connect(dest)
  noise.start(t)
  noise.stop(t + 0.18)

  const osc = ctx.createOscillator()
  const oscGain = ctx.createGain()
  osc.type = 'sine'
  osc.frequency.setValueAtTime(100, t)
  osc.frequency.exponentialRampToValueAtTime(30, t + 0.1)
  oscGain.gain.setValueAtTime(0.6, t)
  oscGain.gain.exponentialRampToValueAtTime(0.001, t + 0.15)
  osc.connect(oscGain)
  oscGain.connect(dest)
  osc.start(t)
  osc.stop(t + 0.2)
}

// ===== MELÓDICOS =====

/** Lead — oscilador com filtro passa-baixa */
export function lead(
  t: number, ctx: AudioContext, dest: AudioNode,
  freq?: number, waveform: OscillatorType = 'sine', volume: number = 0.18
): void {
  if (!freq) return
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  const filter = ctx.createBiquadFilter()
  osc.type = waveform
  osc.frequency.value = freq
  filter.type = 'lowpass'
  filter.frequency.value = freq * 3
  gain.gain.setValueAtTime(volume, t)
  gain.gain.setValueAtTime(volume, t + ST * 0.7)
  gain.gain.exponentialRampToValueAtTime(0.001, t + ST * 0.95)
  osc.connect(filter)
  filter.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + ST)
}

/** Vibrafone — senoidal com tremolo (vibração de volume) */
export function vibes(t: number, ctx: AudioContext, dest: AudioNode, freq?: number): void {
  if (!freq) return
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  osc.type = 'sine'
  osc.frequency.value = freq
  // Tremolo — outro oscilador modulando o volume
  const trem = ctx.createOscillator()
  const tremGain = ctx.createGain()
  trem.type = 'sine'
  trem.frequency.value = 6
  tremGain.gain.value = 0.04
  trem.connect(tremGain)
  tremGain.connect(gain.gain)
  gain.gain.setValueAtTime(0.18, t)
  gain.gain.exponentialRampToValueAtTime(0.001, t + ST * 3)
  osc.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + ST * 3)
  trem.start(t)
  trem.stop(t + ST * 3)
}

/** Assobio — senoidal pura com fade in suave */
export function whistle(t: number, ctx: AudioContext, dest: AudioNode, freq?: number): void {
  if (!freq) return
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  osc.type = 'sine'
  osc.frequency.value = freq
  gain.gain.setValueAtTime(0, t)
  gain.gain.linearRampToValueAtTime(0.09, t + 0.04)
  gain.gain.setValueAtTime(0.09, t + ST * 1.5)
  gain.gain.exponentialRampToValueAtTime(0.001, t + ST * 3)
  osc.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + ST * 3)
}

// ===== PADS E AMBIENTES =====

/** Pad — acorde de senoidais com chorus (duas ondas levemente desafinadas) */
export function pad(t: number, ctx: AudioContext, dest: AudioNode, freqs: number[]): void {
  const dur = ST * TOTAL_STEPS
  freqs.forEach(f => {
    // Onda principal
    const osc = ctx.createOscillator()
    const gain = ctx.createGain()
    osc.type = 'sine'
    osc.frequency.value = f
    gain.gain.setValueAtTime(0, t)
    gain.gain.linearRampToValueAtTime(0.05, t + 0.4)
    gain.gain.setValueAtTime(0.05, t + dur - 0.4)
    gain.gain.linearRampToValueAtTime(0, t + dur)
    osc.connect(gain)
    gain.connect(dest)
    osc.start(t)
    osc.stop(t + dur + 0.1)

    // Chorus — mesma nota levemente desafinada pra dar "largura"
    const osc2 = ctx.createOscillator()
    const gain2 = ctx.createGain()
    osc2.type = 'sine'
    osc2.frequency.value = f * 1.003
    gain2.gain.setValueAtTime(0, t)
    gain2.gain.linearRampToValueAtTime(0.03, t + 0.4)
    gain2.gain.setValueAtTime(0.03, t + dur - 0.4)
    gain2.gain.linearRampToValueAtTime(0, t + dur)
    osc2.connect(gain2)
    gain2.connect(dest)
    osc2.start(t)
    osc2.stop(t + dur + 0.1)
  })
}

/** Sub-bass — grave profundo senoidal */
export function sub(t: number, ctx: AudioContext, dest: AudioNode, freq?: number): void {
  if (!freq) return
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  osc.type = 'sine'
  osc.frequency.value = freq
  gain.gain.setValueAtTime(0.2, t)
  gain.gain.exponentialRampToValueAtTime(0.001, t + ST * 4)
  osc.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + ST * 4)
}

// ===== VOCAIS =====

/** Vocal sintetizado — tipo define timbre (high/low/mid) */
export function vocal(
  t: number, ctx: AudioContext, dest: AudioNode,
  freq?: number, type: 'high' | 'low' | 'mid' = 'mid'
): void {
  if (!freq) return
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  const filter = ctx.createBiquadFilter()

  if (type === 'high') {
    osc.type = 'triangle'
    filter.type = 'bandpass'
    filter.frequency.value = freq
    filter.Q.value = 3
    gain.gain.setValueAtTime(0.13, t)
  } else if (type === 'low') {
    osc.type = 'sawtooth'
    filter.type = 'lowpass'
    filter.frequency.value = freq * 2
    gain.gain.setValueAtTime(0.1, t)
  } else {
    osc.type = 'triangle'
    filter.type = 'bandpass'
    filter.frequency.value = freq
    filter.Q.value = 2
    gain.gain.setValueAtTime(0.11, t)
  }
  osc.frequency.value = freq

  // Vibrato — oscilação suave na frequência
  const vibrato = ctx.createOscillator()
  const vibGain = ctx.createGain()
  vibrato.type = 'sine'
  vibrato.frequency.value = 5.5
  vibGain.gain.value = freq * 0.015
  vibrato.connect(vibGain)
  vibGain.connect(osc.frequency)

  gain.gain.exponentialRampToValueAtTime(0.001, t + ST * 2)
  osc.connect(filter)
  filter.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + ST * 2.5)
  vibrato.start(t)
  vibrato.stop(t + ST * 2.5)
}

/** Vocal sombrio — duas ondas dente-de-serra com detuning */
export function darkVocal(t: number, ctx: AudioContext, dest: AudioNode, freq: number): void {
  const dur = ST * TOTAL_STEPS
  const osc1 = ctx.createOscillator()
  const osc2 = ctx.createOscillator()
  const gain = ctx.createGain()
  osc1.type = 'sawtooth'
  osc1.frequency.value = freq
  osc2.type = 'sawtooth'
  osc2.frequency.value = freq * 1.01
  gain.gain.setValueAtTime(0, t)
  gain.gain.linearRampToValueAtTime(0.06, t + 0.8)
  gain.gain.setValueAtTime(0.06, t + dur - 0.8)
  gain.gain.linearRampToValueAtTime(0, t + dur)
  const filter = ctx.createBiquadFilter()
  filter.type = 'lowpass'
  filter.frequency.value = 200
  osc1.connect(filter)
  osc2.connect(filter)
  filter.connect(gain)
  gain.connect(dest)
  osc1.start(t)
  osc1.stop(t + dur + 0.1)
  osc2.start(t)
  osc2.stop(t + dur + 0.1)
}

/** Vocal sombrio v2 — sawtooth com filtro grave */
export function darkVocal2(t: number, ctx: AudioContext, dest: AudioNode, freq?: number): void {
  if (!freq) return
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  osc.type = 'sawtooth'
  osc.frequency.value = freq
  const filter = ctx.createBiquadFilter()
  filter.type = 'lowpass'
  filter.frequency.value = 300
  gain.gain.setValueAtTime(0.08, t)
  gain.gain.exponentialRampToValueAtTime(0.001, t + ST * 3)
  osc.connect(filter)
  filter.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + ST * 3)
}

// ===== EFEITOS =====

/** Glitch — frequência aleatória ultra-curta */
export function glitch(t: number, ctx: AudioContext, dest: AudioNode): void {
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  osc.type = 'square'
  osc.frequency.setValueAtTime(200 + Math.random() * 2000, t)
  osc.frequency.setValueAtTime(100 + Math.random() * 3000, t + 0.02)
  gain.gain.setValueAtTime(0.07, t)
  gain.gain.exponentialRampToValueAtTime(0.001, t + 0.05)
  osc.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + 0.07)
}

/** Voz robótica — onda quadrada com filtro estreito */
export function robot(t: number, ctx: AudioContext, dest: AudioNode, freq?: number): void {
  if (!freq) return
  const osc = ctx.createOscillator()
  const gain = ctx.createGain()
  osc.type = 'square'
  osc.frequency.value = freq
  const filter = ctx.createBiquadFilter()
  filter.type = 'bandpass'
  filter.frequency.value = freq
  filter.Q.value = 5
  gain.gain.setValueAtTime(0.08, t)
  gain.gain.exponentialRampToValueAtTime(0.001, t + ST * 1.5)
  osc.connect(filter)
  filter.connect(gain)
  gain.connect(dest)
  osc.start(t)
  osc.stop(t + ST * 2)
}

