// ============================================================
// SPRUNKS — Motor de Fases Surpresa
//
// Lógica: a cada 3 ou 4 fases normais, injeta uma fase surpresa
// com personagens do tema favorito da criança.
//
// Exemplo com intervalo 3:
// Fase 1 (normal) → Fase 2 (normal) → Fase 3 (normal) →
// ★ FASE SURPRESA ★ → Fase 4 (normal) → Fase 5 (normal) →
// Fase 6 (normal) → ★ FASE SURPRESA ★ → ...
//
// O engine monta a sequência completa de fases,
// intercalando as normais com as surpresas.
// ============================================================

import type { Character, ChildPreference, Phase } from './types'
import { ALL_PHASES } from './characters'
import { BASE_PHASES, generateSurprisePhase } from './phases'
import { generateSurpriseCharacters } from './character-generator'

interface GamePhase {
  /** Configuração visual da fase */
  config: Phase
  /** Personagens desta fase */
  characters: Character[]
  /** Se é fase surpresa */
  isSurprise: boolean
}

/**
 * Monta a sequência completa de fases do jogo,
 * intercalando fases normais com surpresas baseadas no tema da criança.
 *
 * @param preference - O que a criança gosta (preenchido pelos pais)
 * @param surpriseInterval - A cada quantas fases normais aparece uma surpresa (3 ou 4)
 * @returns Array de GamePhase com fases normais + surpresas intercaladas
 */
export function buildGameSequence(
  preference: ChildPreference | null,
  surpriseInterval: number = 3
): GamePhase[] {
  const sequence: GamePhase[] = []
  let normalCount = 0

  // Fases base do jogo
  for (let i = 0; i < BASE_PHASES.length; i++) {
    // Adiciona fase normal
    sequence.push({
      config: BASE_PHASES[i],
      characters: ALL_PHASES[i],
      isSurprise: false,
    })
    normalCount++

    // A cada N fases normais, injeta uma surpresa (se tem preferência)
    if (preference && normalCount >= surpriseInterval && i < BASE_PHASES.length - 1) {
      const surprisePhase = generateSurprisePhase(preference.favoriteTheme)
      const surpriseChars = generateSurpriseCharacters(preference)

      sequence.push({
        config: surprisePhase,
        characters: surpriseChars,
        isSurprise: true,
      })
      normalCount = 0 // Reseta o contador
    }
  }

  return sequence
}

/**
 * Versão simplificada — jogo sem preferência dos pais.
 * Só as 5 fases normais, sem surpresa.
 */
export function buildDefaultSequence(): GamePhase[] {
  return BASE_PHASES.map((config, i) => ({
    config,
    characters: ALL_PHASES[i],
    isSurprise: false,
  }))
}

/**
 * Calcula quantas fases totais o jogo terá.
 * Útil pra mostrar progresso.
 */
export function calculateTotalPhases(
  preference: ChildPreference | null,
  surpriseInterval: number = 3
): number {
  if (!preference) return BASE_PHASES.length

  const normalPhases = BASE_PHASES.length
  // Surpresas inseridas entre as fases normais (não depois da última)
  const surpriseCount = Math.floor((normalPhases - 1) / surpriseInterval)
  return normalPhases + surpriseCount
}

export type { GamePhase }

