// ============================================================
// SPRUNKS — Motor do Sequenciador
// O "maestro" que rege os instrumentos no tempo certo
//
// Usa lookahead scheduling:
// Em vez de tocar nota por nota, agenda as próximas notas
// 150ms à frente. Assim o som nunca atrasa.
// É como um DJ que já deixa o próximo disco pronto.
// ============================================================

import type { Character } from './types'
import { STEP_DURATION, TOTAL_STEPS } from './notes'

const LOOKAHEAD_MS = 50     // Checa a cada 50ms
const SCHEDULE_AHEAD = 0.15 // Agenda 150ms à frente

let ctx: AudioContext | null = null
let master: GainNode | null = null
let currentStep = 0
let nextStepTime = 0
let timerId: ReturnType<typeof setTimeout> | null = null
let isPlaying = false

/** Callbacks pra UI reagir ao sequenciador */
let onStepCallback: ((step: number) => void) | null = null

/**
 * Conecta o sequenciador ao AudioContext.
 * Precisa ser chamado antes de start().
 */
export function connectSequencer(audioCtx: AudioContext, masterGain: GainNode): void {
  ctx = audioCtx
  master = masterGain
}

/**
 * Registra callback chamado a cada step (pra atualizar UI).
 */
export function onStep(callback: (step: number) => void): void {
  onStepCallback = callback
}

/**
 * Inicia o sequenciador.
 * Começa a agendar notas no tempo.
 */
export function startSequencer(): void {
  if (isPlaying || !ctx) return
  isPlaying = true
  currentStep = 0
  nextStepTime = ctx.currentTime + 0.05
  schedule()
}

/**
 * Para o sequenciador.
 */
export function stopSequencer(): void {
  isPlaying = false
  if (timerId) {
    clearTimeout(timerId)
    timerId = null
  }
}

/**
 * Retorna se está tocando.
 */
export function isSequencerPlaying(): boolean {
  return isPlaying
}

/**
 * Retorna o step atual.
 */
export function getCurrentStep(): number {
  return currentStep
}

/**
 * Agenda os steps — o coração do sequenciador.
 * Roda em loop via setTimeout, sempre olhando à frente.
 */
function schedule(): void {
  if (!isPlaying || !ctx) return

  while (nextStepTime < ctx.currentTime + SCHEDULE_AHEAD) {
    // Notifica a UI com delay sincronizado
    const stepToShow = currentStep
    const delayMs = (nextStepTime - ctx.currentTime) * 1000
    setTimeout(() => {
      if (onStepCallback) onStepCallback(stepToShow)
    }, Math.max(0, delayMs))

    // Avança pro próximo step
    currentStep = (currentStep + 1) % TOTAL_STEPS
    nextStepTime += STEP_DURATION
  }

  timerId = setTimeout(schedule, LOOKAHEAD_MS)
}

/**
 * Toca um step específico — chama os synths dos personagens ativos.
 * Chamado pelo componente do jogo.
 */
export function playStep(
  stepIndex: number,
  time: number,
  characters: Character[],
  activeIndices: Set<number>
): void {
  if (!ctx || !master) return

  activeIndices.forEach(charIndex => {
    const char = characters[charIndex]
    if (!char || !char.pattern[stepIndex]) return

    const noteFreq = char.notes ? char.notes[stepIndex] : undefined
    char.synth(time, ctx!, master!, noteFreq)
  })
}

/**
 * Versão integrada — sequenciador que já toca os personagens.
 * Conecta automaticamente o agendamento com a execução dos sons.
 */
export function startIntegratedSequencer(
  audioCtx: AudioContext,
  masterGain: GainNode,
  getCharacters: () => Character[],
  getActiveIndices: () => Set<number>,
  onStepUI?: (step: number) => void
): void {
  if (isPlaying) return

  ctx = audioCtx
  master = masterGain
  isPlaying = true
  currentStep = 0
  nextStepTime = audioCtx.currentTime + 0.05

  function scheduleIntegrated(): void {
    if (!isPlaying || !ctx || !master) return

    while (nextStepTime < ctx.currentTime + SCHEDULE_AHEAD) {
      const s = currentStep
      const t = nextStepTime

      // Toca os sons dos personagens ativos
      const chars = getCharacters()
      const actives = getActiveIndices()
      actives.forEach(ci => {
        const ch = chars[ci]
        if (!ch || !ch.pattern[s]) return
        const nt = ch.notes ? ch.notes[s] : undefined
        ch.synth(t, ctx!, master!, nt)
      })

      // Notifica UI
      const delayMs = (t - ctx!.currentTime) * 1000
      setTimeout(() => {
        if (onStepUI) onStepUI(s)
      }, Math.max(0, delayMs))

      currentStep = (currentStep + 1) % TOTAL_STEPS
      nextStepTime += STEP_DURATION
    }

    timerId = setTimeout(scheduleIntegrated, LOOKAHEAD_MS)
  }

  scheduleIntegrated()
}

/**
 * Reseta o sequenciador pro estado inicial.
 */
export function resetSequencer(): void {
  stopSequencer()
  currentStep = 0
  nextStepTime = 0
}

