// ============================================================
// SPRUNKS — Componente de Personagem
// Cada bonequinho no palco — clica pra ativar/desativar
// ============================================================

'use client'

import type { Character as CharacterType } from '@/lib/sprunki/types'

interface CharacterProps {
  character: CharacterType
  index: number
  isActive: boolean
  onToggle: (index: number) => void
}

export default function Character({ character, index, isActive, onToggle }: CharacterProps) {
  return (
    <div
      className={`chr ${isActive ? 'on' : 'off'}`}
      style={{ '--c': character.color } as React.CSSProperties}
      onClick={() => onToggle(index)}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault()
          onToggle(index)
        }
      }}
      aria-label={`${character.name} - ${character.description}. ${isActive ? 'Ativo' : 'Inativo'}`}
    >
      <div
        className="chr-svg"
        style={{ position: 'relative' }}
        dangerouslySetInnerHTML={{ __html: character.svg() + '<div class="chr-note">♪</div>' }}
      />
      <div className="chr-name">{character.name}</div>
      <div className="chr-type">
        {character.isSurprise && '★ '}
        {character.type}
      </div>
    </div>
  )
}

// ============================================================
// SPRUNKS — Palco dos Personagens
// Grid onde os Sprunks ficam — o "palco do show"
// ============================================================

'use client'

import type { Character as CharacterType } from '@/lib/sprunki/types'
import Character from './Character'

interface StageProps {
  characters: CharacterType[]
  activeSet: Set<number>
  onToggle: (index: number) => void
}

export default function Stage({ characters, activeSet, onToggle }: StageProps) {
  return (
    <div id="stage">
      {characters.map((char, i) => (
        <Character
          key={`${char.name}-${i}`}
          character={char}
          index={i}
          isActive={activeSet.has(i)}
          onToggle={onToggle}
        />
      ))}
    </div>
  )
}

// ============================================================
// SPRUNKS — Indicador de Steps
// Os pontinhos que mostram qual beat tá tocando agora
// Como as luzes de um semáforo musical
// ============================================================

'use client'

import { TOTAL_STEPS } from '@/lib/sprunki/notes'

interface StepIndicatorProps {
  currentStep: number
}

export default function StepIndicator({ currentStep }: StepIndicatorProps) {
  return (
    <div id="step-row">
      {Array.from({ length: TOTAL_STEPS }, (_, i) => (
        <div
          key={i}
          className={`sdot ${i === currentStep ? 'on' : ''}`}
        />
      ))}
    </div>
  )
}

