// ============================================================
// SPRUNKS — Visualizador de Áudio
// Barrinhas que dançam com a música (tipo equalizador)
// ============================================================

'use client'

import { useEffect, useRef } from 'react'

interface VisualizerProps {
  activeCount: number
  totalCount: number
  currentStep: number
  phaseIndex: number
}

const BAR_COUNT = 28

export default function Visualizer({ activeCount, totalCount, currentStep, phaseIndex }: VisualizerProps) {
  const barsRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    if (!barsRef.current) return
    const bars = barsRef.current.children

    if (activeCount === 0) {
      // Sem som → barras mortas
      for (let i = 0; i < bars.length; i++) {
        const bar = bars[i] as HTMLDivElement
        bar.style.height = '2px'
        bar.style.background = 'rgba(201,168,76,0.1)'
      }
      return
    }

    const intensity = activeCount / totalCount
    const isBeat = currentStep % 4 === 0

    for (let i = 0; i < bars.length; i++) {
      const bar = bars[i] as HTMLDivElement
      const h = 2 + Math.random() * 25 * intensity * (isBeat ? 1.5 : 0.6)
      bar.style.height = `${h}px`
      bar.style.background = `hsl(${(40 + i * 3) % 60},70%,${50 - phaseIndex * 5}%)`
    }
  }, [currentStep, activeCount, totalCount, phaseIndex])

  return (
    <div id="viz" ref={barsRef}>
      {Array.from({ length: BAR_COUNT }, (_, i) => (
        <div key={i} className="vbar" />
      ))}
    </div>
  )
}

