// ============================================================
// SPRUNKS — HUD (Head-Up Display)
// Informações no topo da tela — fase atual, placar, progresso
// ============================================================

'use client'

interface HUDProps {
  phaseNumber: number
  activeCount: number
  totalCount: number
  phaseName: string
  isSurprise: boolean
}

export default function HUD({ phaseNumber, activeCount, totalCount, phaseName, isSurprise }: HUDProps) {
  const percentage = totalCount > 0 ? (activeCount / totalCount) * 100 : 0
  const isComplete = activeCount === totalCount && totalCount > 0

  return (
    <>
      {/* Barra superior */}
      <div id="hud">
        <span id="hud-phase">
          {isSurprise ? '★ SURPRESA' : `FASE ${phaseNumber}`}
        </span>
        <span id="hud-title">{phaseName}</span>
        <span id="hud-score">{activeCount}/{totalCount}</span>
      </div>

      {/* Barra de progresso */}
      <div id="prog-wrap">
        <div
          id="prog-bar"
          style={{ width: `${percentage}%` }}
        />
      </div>
      <div id="prog-text">
        {isComplete ? 'COMPLETO!' : 'Ative todos para completar'}
      </div>
    </>
  )
}

