// ============================================================
// SPRUNKS — Navegação entre Fases
// Setas pra ir e voltar entre as fases do jogo
// ============================================================

'use client'

interface PhaseNavProps {
  currentPhase: number
  totalPhases: number
  phaseName: string
  canGoRight: boolean
  onNavigate: (direction: -1 | 1) => void
}

export default function PhaseNav({ currentPhase, totalPhases, phaseName, canGoRight, onNavigate }: PhaseNavProps) {
  const canGoLeft = currentPhase > 0

  return (
    <div id="phase-nav">
      <button
        className={`phase-arrow ${!canGoLeft ? 'disabled' : ''}`}
        onClick={() => onNavigate(-1)}
        disabled={!canGoLeft}
        aria-label="Fase anterior"
      >
        ◀
      </button>
      <span id="phase-label">{phaseName}</span>
      <button
        className={`phase-arrow ${!canGoRight ? 'disabled' : ''}`}
        onClick={() => onNavigate(1)}
        disabled={!canGoRight}
        aria-label="Próxima fase"
      >
        ▶
      </button>
    </div>
  )
}

