// ============================================================
// SPRUNKS — Cenário de Fundo
// Céu, montanhas, colinas e chão quadriculado
// Tudo gerado por código — sem imagens
// ============================================================

'use client'

import { useEffect, useRef } from 'react'
import type { Phase } from '@/lib/sprunki/types'

interface BackgroundProps {
  phase: Phase
  phaseIndex: number
}

export default function Background({ phase, phaseIndex }: BackgroundProps) {
  const mountainsRef = useRef<HTMLDivElement>(null)
  const hillsRef = useRef<HTMLDivElement>(null)
  const skyRef = useRef<HTMLDivElement>(null)
  const builtRef = useRef(false)

  // Gera montanhas, colinas e estrelas uma vez
  useEffect(() => {
    if (builtRef.current) return
    builtRef.current = true

    // Montanhas
    if (mountainsRef.current) {
      for (let i = 0; i < 12; i++) {
        const m = document.createElement('div')
        m.className = 'mountain'
        m.style.left = `${i * 160}px`
        m.style.borderBottomWidth = `${60 + Math.random() * 80}px`
        m.style.borderLeftWidth = `${50 + Math.random() * 60}px`
        m.style.borderRightWidth = `${50 + Math.random() * 60}px`
        mountainsRef.current.appendChild(m)
      }
    }

    // Colinas
    if (hillsRef.current) {
      for (let i = 0; i < 16; i++) {
        const h = document.createElement('div')
        h.className = 'hill'
        h.style.left = `${i * 120}px`
        h.style.width = `${80 + Math.random() * 80}px`
        h.style.height = `${30 + Math.random() * 40}px`
        hillsRef.current.appendChild(h)
      }
    }

    // Estrelas
    if (skyRef.current) {
      for (let i = 0; i < 40; i++) {
        const s = document.createElement('div')
        s.className = 'star'
        s.style.left = `${Math.random() * 100}%`
        s.style.top = `${Math.random() * 60}%`
        const size = `${1 + Math.random() * 2}px`
        s.style.width = size
        s.style.height = size
        skyRef.current.appendChild(s)
      }
    }
  }, [])

  // Filtro de atmosfera conforme a fase escurece
  let sceneFilter = 'none'
  if (phaseIndex >= 3) {
    sceneFilter = 'saturate(0.4) contrast(1.2) brightness(0.8)'
  } else if (phaseIndex >= 2) {
    sceneFilter = 'saturate(0.7) contrast(1.1)'
  }

  return (
    <div id="scene" style={{ filter: sceneFilter }}>
      {/* Céu */}
      <div
        id="sky"
        ref={skyRef}
        style={{
          background: `linear-gradient(180deg, ${phase.skyGradient[0]} 0%, ${phase.skyGradient[1]} 40%, ${phase.skyGradient[2]} 100%)`,
        }}
      />

      {/* Montanhas (parallax lento) */}
      <div id="mountains" ref={mountainsRef} />

      {/* Colinas (parallax médio) */}
      <div id="hills" ref={hillsRef} />

      {/* Chão */}
      <div id="ground">
        <div id="grass-line" style={{ background: phase.grassColor }} />
        <div
          id="checkerboard"
          style={{
            backgroundImage: `repeating-conic-gradient(${phase.checkerColor1} 0% 25%, ${phase.checkerColor2} 0% 50%)`,
          }}
        />
      </div>

      {/* Palmeiras decorativas */}
      <div className="palm" style={{ left: '10%' }}>
        <div className="palm-leaves">
          <div className="palm-leaf" />
          <div className="palm-leaf" />
          <div className="palm-leaf" />
        </div>
        <div className="palm-trunk" />
      </div>
      <div className="palm" style={{ left: '85%' }}>
        <div className="palm-leaves">
          <div className="palm-leaf" />
          <div className="palm-leaf" />
          <div className="palm-leaf" />
        </div>
        <div className="palm-trunk" />
      </div>
    </div>
  )
}

