// ============================================================
// SPRUNKS — Tela de Configuração dos Pais
// Os pais dizem o que a criança gosta,
// e o jogo cria fases surpresa personalizadas.
// ============================================================

'use client'

import { useState } from 'react'
import type { ChildPreference } from '@/lib/sprunki/types'
import { getAvailableThemes } from '@/lib/sprunki/character-generator'

interface ParentSetupProps {
  onComplete: (preference: ChildPreference) => void
  onSkip: () => void
}

const THEMES = getAvailableThemes()

const THEME_LABELS: Record<string, string> = {
  sonic: 'Sonic',
  mario: 'Mario',
  pokemon: 'Pokémon',
  'dragon-ball': 'Dragon Ball',
  minecraft: 'Minecraft',
  naruto: 'Naruto',
  'one-piece': 'One Piece',
  roblox: 'Roblox',
  fortnite: 'Fortnite',
  'hello-kitty': 'Hello Kitty',
}

const COLOR_OPTIONS = [
  { value: '#FF0000', label: 'Vermelho' },
  { value: '#0066FF', label: 'Azul' },
  { value: '#00CC44', label: 'Verde' },
  { value: '#FFD700', label: 'Amarelo' },
  { value: '#FF69B4', label: 'Rosa' },
  { value: '#9B5DE5', label: 'Roxo' },
  { value: '#FF6600', label: 'Laranja' },
  { value: '#00CCCC', label: 'Turquesa' },
]

export default function ParentSetup({ onComplete, onSkip }: ParentSetupProps) {
  const [childName, setChildName] = useState('')
  const [selectedTheme, setSelectedTheme] = useState('')
  const [selectedColors, setSelectedColors] = useState<string[]>([])
  const [description, setDescription] = useState('')
  const [step, setStep] = useState(0)

  function toggleColor(color: string) {
    setSelectedColors(prev =>
      prev.includes(color)
        ? prev.filter(c => c !== color)
        : prev.length < 2 ? [...prev, color] : [prev[1], color]
    )
  }

  function handleSubmit() {
    onComplete({
      childName: childName || 'Jogador',
      favoriteTheme: selectedTheme,
      favoriteColors: selectedColors,
      parentDescription: description,
    })
  }

  return (
    <div className="parent-setup">
      <div className="setup-card">
        {step === 0 && (
          <>
            <h2 className="setup-title">Personalize o Jogo!</h2>
            <p className="setup-desc">
              Conte o que seu filho(a) mais gosta.
              O jogo vai criar fases surpresa especiais!
            </p>

            <label className="setup-label">Nome da criança</label>
            <input
              type="text"
              className="setup-input"
              placeholder="Ex: Lucas"
              value={childName}
              onChange={(e) => setChildName(e.target.value)}
              maxLength={20}
            />

            <div className="setup-actions">
              <button className="setup-btn secondary" onClick={onSkip}>
                Pular
              </button>
              <button
                className="setup-btn primary"
                onClick={() => setStep(1)}
              >
                Próximo
              </button>
            </div>
          </>
        )}

        {step === 1 && (
          <>
            <h2 className="setup-title">Personagem Favorito</h2>
            <p className="setup-desc">
              Qual universo {childName || 'a criança'} mais curte?
            </p>

            <div className="theme-grid">
              {THEMES.map(theme => (
                <button
                  key={theme}
                  className={`theme-btn ${selectedTheme === theme ? 'selected' : ''}`}
                  onClick={() => setSelectedTheme(theme)}
                >
                  {THEME_LABELS[theme] || theme}
                </button>
              ))}
            </div>

            <label className="setup-label" style={{ marginTop: '12px' }}>
              Ou descreva com suas palavras:
            </label>
            <input
              type="text"
              className="setup-input"
              placeholder="Ex: gosta de dinossauros e robôs"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />

            <div className="setup-actions">
              <button className="setup-btn secondary" onClick={() => setStep(0)}>
                Voltar
              </button>
              <button
                className="setup-btn primary"
                onClick={() => setStep(2)}
              >
                Próximo
              </button>
            </div>
          </>
        )}

        {step === 2 && (
          <>
            <h2 className="setup-title">Cores Favoritas</h2>
            <p className="setup-desc">
              Escolha até 2 cores que {childName || 'a criança'} mais gosta.
            </p>

            <div className="color-grid">
              {COLOR_OPTIONS.map(({ value, label }) => (
                <button
                  key={value}
                  className={`color-btn ${selectedColors.includes(value) ? 'selected' : ''}`}
                  style={{ '--btn-color': value } as React.CSSProperties}
                  onClick={() => toggleColor(value)}
                >
                  <div className="color-swatch" style={{ background: value }} />
                  <span>{label}</span>
                </button>
              ))}
            </div>

            <div className="setup-actions">
              <button className="setup-btn secondary" onClick={() => setStep(1)}>
                Voltar
              </button>
              <button
                className="setup-btn primary"
                onClick={handleSubmit}
              >
                Começar!
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  )
}

