// ============================================================
// SPRUNKS — Overlays (Transição de Fase + Tela de Vitória)
// ============================================================

'use client'

interface TransitionOverlayProps {
  visible: boolean
  phaseName: string
  phaseSubtitle: string
  isSurprise: boolean
}

export function TransitionOverlay({ visible, phaseName, phaseSubtitle, isSurprise }: TransitionOverlayProps) {
  return (
    <div id="overlay" className={visible ? 'show' : ''}>
      <div id="overlay-text">
        {isSurprise && '★ '}
        {phaseName}
        {isSurprise && ' ★'}
      </div>
      <div id="overlay-sub">{phaseSubtitle}</div>
    </div>
  )
}

interface WinScreenProps {
  visible: boolean
  onRestart: () => void
}

export function WinScreen({ visible, onRestart }: WinScreenProps) {
  return (
    <div id="win" className={visible ? 'show' : ''}>
      <h1>PARABÉNS!</h1>
      <p>Você completou todas as fases!</p>
