// ============================================================
// SPRUNKS — Componente Principal do Jogo
// Cola tudo junto: sequenciador + personagens + fases + surpresas
// ============================================================

'use client'

import { useState, useCallback, useRef, useEffect } from 'react'
import type { ChildPreference, Character } from '@/lib/sprunki/types'
import { initAudio, destroyAudio } from '@/lib/sprunki/audio-context'
import { startIntegratedSequencer, stopSequencer, resetSequencer } from '@/lib/sprunki/sequencer'
import { buildGameSequence, buildDefaultSequence, type GamePhase } from '@/lib/sprunki/surprise-engine'
import Background from './Background'
import HUD from './HUD'
import StepIndicator from './StepIndicator'
import Visualizer from './Visualizer'
import Stage from './Stage'
import PhaseNav from './PhaseNav'
import { TransitionOverlay, WinScreen } from './Overlay'
import ParentSetup from './ParentSetup'

/** Intervalo de fases normais entre cada surpresa */
const SURPRISE_INTERVAL = 3

export default function SprunksGame() {
  // Estado do setup dos pais
  const [preference, setPreference] = useState<ChildPreference | null>(null)
  const [setupDone, setSetupDone] = useState(false)

  // Estado do jogo
  const [phaseIndex, setPhaseIndex] = useState(0)
  const [activeSet, setActiveSet] = useState<Set<number>>(new Set())
  const [phasesCompleted, setPhasesCompleted] = useState<boolean[]>([])
  const [currentStep, setCurrentStep] = useState(0)
  const [showTransition, setShowTransition] = useState(false)
  const [showWin, setShowWin] = useState(false)
  const [transitionTarget, setTransitionTarget] = useState<{ name: string; sub: string; surprise: boolean }>({
    name: '', sub: '', surprise: false,
  })

  // Referências mutáveis (o sequenciador precisa ler em tempo real)
  const gameSequenceRef = useRef<GamePhase[]>([])
  const activeSetRef = useRef<Set<number>>(new Set())
  const phaseIndexRef = useRef(0)
  const isPlayingRef = useRef(false)

  // Monta a sequência de fases
  const gameSequence = setupDone
    ? (preference ? buildGameSequence(preference, SURPRISE_INTERVAL) : buildDefaultSequence())
    : []

  // Atualiza ref
  gameSequenceRef.current = gameSequence

  // Fase atual
  const currentPhase = gameSequence[phaseIndex]
  const characters = currentPhase?.characters || []

  // Inicializa phasesCompleted quando sequência muda
  useEffect(() => {
    if (gameSequence.length > 0 && phasesCompleted.length !== gameSequence.length) {
      setPhasesCompleted(new Array(gameSequence.length).fill(false))
    }
  }, [gameSequence.length, phasesCompleted.length])

  // Sync ref com state
  useEffect(() => {
    activeSetRef.current = activeSet
    phaseIndexRef.current = phaseIndex
  }, [activeSet, phaseIndex])

  // Limpa áudio ao desmontar
  useEffect(() => {
    return () => {
      stopSequencer()
      destroyAudio()
    }
  }, [])

  // Teclado
  useEffect(() => {
    function handleKey(e: KeyboardEvent) {
      if (!setupDone) return
      if (e.code === 'ArrowLeft') {
        e.preventDefault()
        handleNavigate(-1)
      } else if (e.code === 'ArrowRight') {
        e.preventDefault()
        handleNavigate(1)
      } else if (e.key >= '1' && e.key <= '6') {
        const i = parseInt(e.key) - 1
        if (i < characters.length) handleToggle(i)
      }
    }
    window.addEventListener('keydown', handleKey)
    return () => window.removeEventListener('keydown', handleKey)
  }, [setupDone, characters.length, phaseIndex, phasesCompleted])

  // Partículas ao ativar personagem
  function spawnParticles(color: string, targetEl: Element | null) {
    if (!targetEl) return
    const r = targetEl.getBoundingClientRect()
    for (let j = 0; j < 5; j++) {
      const p = document.createElement('div')
      p.className = 'ptcl'
      p.style.background = color
      p.style.left = `${r.left + r.width / 2 + (Math.random() - 0.5) * 40}px`
      p.style.top = `${r.top + (Math.random() - 0.5) * 20}px`
      const size = `${2 + Math.random() * 4}px`
      p.style.width = size
      p.style.height = size
      document.body.appendChild(p)
      setTimeout(() => p.remove(), 1500)
    }
  }

  // Toggle personagem on/off
  const handleToggle = useCallback((index: number) => {
    const { ctx, master } = initAudio()

    setActiveSet(prev => {
      const next = new Set(prev)
      if (next.has(index)) {
        next.delete(index)
        if (next.size === 0) {
          stopSequencer()
          isPlayingRef.current = false
        }
      } else {
        next.add(index)
        // Partículas
        const els = document.querySelectorAll('.chr')
        if (els[index]) {
          const char = gameSequenceRef.current[phaseIndexRef.current]?.characters[index]
          if (char) spawnParticles(char.color, els[index])
        }
        // Inicia sequenciador se não tá tocando
        if (!isPlayingRef.current) {
          isPlayingRef.current = true
          startIntegratedSequencer(
            ctx, master,
            () => gameSequenceRef.current[phaseIndexRef.current]?.characters || [],
            () => activeSetRef.current,
            (step) => setCurrentStep(step)
          )
        }
      }

      // Checa se completou a fase
      const phaseChars = gameSequenceRef.current[phaseIndexRef.current]?.characters || []
      if (next.size === phaseChars.length && phaseChars.length > 0) {
        setTimeout(() => {
          if (activeSetRef.current.size === phaseChars.length) {
            handlePhaseComplete()
          }
        }, 3000) // Espera 2 loops completos antes de avançar
      }

      return next
    })
  }, [])

  // Fase completa
  function handlePhaseComplete() {
    const pi = phaseIndexRef.current

    setPhasesCompleted(prev => {
      const next = [...prev]
      next[pi] = true
      return next
    })

    // Última fase → vitória
    if (pi >= gameSequenceRef.current.length - 1) {
      setShowWin(true)
      stopSequencer()
      isPlayingRef.current = false
      return
    }

    // Transição pra próxima fase
    const nextPhase = gameSequenceRef.current[pi + 1]
    if (nextPhase) {
      setTransitionTarget({
        name: nextPhase.config.name,
        sub: nextPhase.config.subtitle,
        surprise: nextPhase.isSurprise,
      })
      setShowTransition(true)

      setTimeout(() => {
        stopSequencer()
        isPlayingRef.current = false
        setActiveSet(new Set())
        setPhaseIndex(pi + 1)
        setTimeout(() => setShowTransition(false), 500)
      }, 1500)
    }
  }

  // Navegação manual entre fases
  function handleNavigate(dir: -1 | 1) {
    const target = phaseIndex + dir
    if (target < 0 || target >= gameSequence.length) return
    // Só avança se a fase anterior foi completada
    if (dir > 0 && !phasesCompleted[phaseIndex]) return

    stopSequencer()
    isPlayingRef.current = false
    setActiveSet(new Set())
    setPhaseIndex(target)
  }

  // Reiniciar jogo
  function handleRestart() {
    stopSequencer()
    resetSequencer()
    isPlayingRef.current = false
    setPhaseIndex(0)
    setActiveSet(new Set())
    setPhasesCompleted(new Array(gameSequence.length).fill(false))
    setShowWin(false)
    setCurrentStep(0)
  }

  // Setup dos pais
  function handleSetupComplete(pref: ChildPreference) {
    setPreference(pref)
    setSetupDone(true)
  }

  function handleSetupSkip() {
    setSetupDone(true)
  }

  // Tela de setup
  if (!setupDone) {
    return <ParentSetup onComplete={handleSetupComplete} onSkip={handleSetupSkip} />
  }

  // Jogo sem fases (safety check)
  if (!currentPhase) return null

  const canGoRight = phaseIndex < gameSequence.length - 1 && phasesCompleted[phaseIndex]

  return (
    <>
      <Background phase={currentPhase.config} phaseIndex={phaseIndex} />

      <div id="game-ui">
        <HUD
          phaseNumber={phaseIndex + 1}
          activeCount={activeSet.size}
          totalCount={characters.length}
          phaseName={currentPhase.config.name}
          isSurprise={currentPhase.isSurprise}
        />

        <StepIndicator currentStep={currentStep} />

        <Visualizer
          activeCount={activeSet.size}
          totalCount={characters.length}
          currentStep={currentStep}
          phaseIndex={phaseIndex}
        />

        <Stage
          characters={characters}
          activeSet={activeSet}
          onToggle={handleToggle}
        />

        <PhaseNav
          currentPhase={phaseIndex}
          totalPhases={gameSequence.length}
          phaseName={currentPhase.config.name}
          canGoRight={canGoRight}
          onNavigate={handleNavigate}
        />
      </div>

      <TransitionOverlay
        visible={showTransition}
        phaseName={transitionTarget.name}
        phaseSubtitle={transitionTarget.sub}
        isSurprise={transitionTarget.surprise}
      />

      <WinScreen visible={showWin} onRestart={handleRestart} />
    </>
  )
}

