input.content:
/* ============================================================
   SPRUNKS — Estilos Globais do Jogo
   Estética retrô Sega dos anos 90 + pixel art feel
   ============================================================ */

@import url('https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap');

/* ===== RESET & BASE ===== */
* { margin: 0; padding: 0; box-sizing: border-box; }

body {
  font-family: 'Press Start 2P', cursive;
  background: #000;
  color: #fff;
  min-height: 100vh;
  overflow: hidden;
  user-select: none;
  image-rendering: pixelated;
}

/* ===== CENÁRIO ===== */
#scene {
  position: fixed;
  top: 0; left: 0;
  width: 100%; height: 100%;
  overflow: hidden;
  transition: filter 2s ease;
}

#sky {
  position: absolute;
  top: 0; left: 0;
  width: 100%; height: 55%;
  background: linear-gradient(180deg, #2040A0 0%, #4080D0 40%, #80C0F0 100%);
  transition: background 2s ease;
}

.star {
  position: absolute;
  background: #fff;
  border-radius: 50%;
  opacity: 0;
  transition: opacity 2s ease;
  animation: twinkle 3s ease-in-out infinite alternate;
}

@keyframes twinkle {
  0% { opacity: 0.3; }
  100% { opacity: 1; }
}

#mountains {
  position: absolute;
  bottom: 42%; left: 0;
  width: 200%; height: 20%;
  animation: parallax-slow 30s linear infinite;
}

.mountain {
  position: absolute;
  bottom: 0;
  width: 0; height: 0;
  border-left: 80px solid transparent;
  border-right: 80px solid transparent;
  border-bottom: 100px solid #304878;
  opacity: 0.6;
}

#hills {
  position: absolute;
  bottom: 30%; left: 0;
  width: 200%; height: 20%;
  animation: parallax-mid 20s linear infinite;
}

.hill {
  position: absolute;
  bottom: 0;
  width: 120px; height: 60px;
  background: #206030;
  border-radius: 60px 60px 0 0;
}

@keyframes parallax-slow {
  0% { transform: translateX(0); }
  100% { transform: translateX(-50%); }
}

@keyframes parallax-mid {
  0% { transform: translateX(0); }
  100% { transform: translateX(-50%); }
}

#ground {
  position: absolute;
  bottom: 0; left: 0;
  width: 100%; height: 32%;
  overflow: hidden;
}

#grass-line {
  position: absolute;
  top: 0; left: 0;
  width: 100%; height: 8px;
  background: #40C040;
  z-index: 2;
}

#checkerboard {
  position: absolute;
  top: 8px; left: 0;
  width: 100%; height: 100%;
  background-image: repeating-conic-gradient(#8B5E34 0% 25%, #A0703C 0% 50%);
  background-size: 40px 40px;
  transform: perspective(400px) rotateX(15deg);
  transform-origin: top center;
}

/* Palmeiras */
.palm {
  position: absolute;
  bottom: 30%;
  z-index: 1;
}

.palm-trunk {
  width: 8px; height: 60px;
  background: #6B4226;
  margin: 0 auto;
  border-radius: 4px;
  transform: rotate(3deg);
}

.palm-leaves {
  width: 50px; height: 25px;
  position: relative;
  margin-left: -21px;
}

.palm-leaf {
  position: absolute;
  width: 24px; height: 20px;
  background: #208030;
  border-radius: 0 50% 50% 50%;
}

.palm-leaf:nth-child(1) { transform: rotate(-40deg); left: 0; }
.palm-leaf:nth-child(2) { transform: rotate(-10deg); left: 12px; }
.palm-leaf:nth-child(3) { transform: rotate(20deg); left: 24px; }

/* ===== GAME UI ===== */
#game-ui {
  position: relative;
  z-index: 10;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding-top: 12px;
}

/* HUD */
#hud {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 90%;
  max-width: 800px;
  padding: 8px 16px;
  background: rgba(0, 0, 0, 0.7);
  border: 2px solid #C9A84C;
  border-radius: 4px;
  margin-bottom: 8px;
}

#hud-phase, #hud-score, #hud-title { color: #C9A84C; }
#hud-phase, #hud-score { font-size: 10px; }
#hud-title { font-size: 12px; color: #fff; }

/* Step dots */
#step-row {
  display: flex;
  gap: 3px;
  margin-bottom: 6px;
  height: 6px;
}

.sdot {
  width: 6px; height: 6px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.1);
  transition: background 0.05s;
}

.sdot.on { background: #C9A84C; }

/* Visualizador */
#viz {
  display: flex;
  gap: 2px;
  height: 28px;
  align-items: flex-end;
  margin-bottom: 6px;
}

.vbar {
  width: 3px;
  background: rgba(201, 168, 76, 0.15);
  border-radius: 1px;
  min-height: 2px;
  transition: height 0.08s;
}

/* ===== PERSONAGENS ===== */
#stage {
  display: flex;
  justify-content: center;
  align-items: flex-end;
  gap: 8px;
  margin-bottom: 8px;
  min-height: 200px;
  flex-wrap: wrap;
  padding: 0 10px;
}

.chr {
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  transition: all 0.2s;
