  padding: 20px;
}

.setup-card {
  background: rgba(20, 20, 40, 0.9);
  border: 2px solid #C9A84C;
  border-radius: 8px;
  padding: 24px;
  max-width: 440px;
  width: 100%;
}

.setup-title {
  font-size: 14px;
  color: #C9A84C;
  margin-bottom: 8px;
  text-align: center;
}

.setup-desc {
  font-size: 7px;
  color: #888;
  margin-bottom: 16px;
  text-align: center;
  line-height: 1.6;
}

.setup-label {
  display: block;
  font-size: 7px;
  color: #C9A84C;
  margin-bottom: 6px;
}

.setup-input {
  width: 100%;
  background: rgba(0, 0, 0, 0.5);
  border: 1px solid #444;
  border-radius: 4px;
  padding: 8px 12px;
  color: #fff;
  font-family: 'Press Start 2P', cursive;
  font-size: 8px;
  margin-bottom: 16px;
  outline: none;
  transition: border-color 0.2s;
}

.setup-input:focus {
  border-color: #C9A84C;
}

.setup-input::placeholder {
  color: #555;
}

/* Grid de temas */
.theme-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 6px;
  margin-bottom: 8px;
}

.theme-btn {
  font-family: 'Press Start 2P', cursive;
  font-size: 7px;
  padding: 8px 6px;
  background: rgba(0, 0, 0, 0.5);
  border: 1px solid #333;
  border-radius: 4px;
  color: #888;
  cursor: pointer;
  transition: all 0.15s;
}

.theme-btn:hover { border-color: #666; color: #aaa; }

.theme-btn.selected {
  border-color: #C9A84C;
  color: #C9A84C;
  background: rgba(201, 168, 76, 0.1);
}

/* Grid de cores */
.color-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 6px;
  margin-bottom: 16px;
}

.color-btn {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  padding: 6px;
  background: rgba(0, 0, 0, 0.5);
  border: 1px solid #333;
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.15s;
  font-family: 'Press Start 2P', cursive;
  font-size: 5px;
  color: #666;
}

.color-btn:hover { border-color: #666; }

.color-btn.selected {
  border-color: var(--btn-color);
  color: #fff;
  background: rgba(255, 255, 255, 0.05);
}

.color-swatch {
  width: 24px; height: 24px;
  border-radius: 50%;
  border: 2px solid transparent;
  transition: border-color 0.15s;
}

.color-btn.selected .color-swatch {
  border-color: #fff;
}

/* Botões de ação */
.setup-actions {
