  gap: 8px;
  justify-content: center;
  margin-top: 16px;
}

.setup-btn {
  font-family: 'Press Start 2P', cursive;
  font-size: 8px;
  padding: 10px 16px;
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.15s;
}

.setup-btn.primary {
  background: #C9A84C;
  color: #000;
  border: 2px solid #C9A84C;
}

.setup-btn.primary:hover {
  background: #F7C948;
}

.setup-btn.secondary {
  background: transparent;
  color: #666;
  border: 2px solid #333;
}

.setup-btn.secondary:hover {
  border-color: #666;
  color: #999;
}

