  cursor: pointer;
  transition: all 0.15s;
}

.setup-btn.primary {
  background: #C9A84C;
  color: #000;
  border: 2px solid #C9A84C;
}

.setup-btn.primary:hover {
  background: #F7C948;
}

.setup-btn.secondary {
  background: transparent;
  color: #666;
  border: 2px solid #333;
}

.setup-btn.secondary:hover {
  border-color: #666;
  color: #999;
}

/* ===== RESPONSIVO ===== */
@media (max-width: 600px) {
  .chr-svg { width: 54px; height: 82px; }
  #stage { gap: 4px; min-height: 140px; }
  .chr-name { font-size: 5px; }
  #hud { padding: 6px 10px; }
  #hud-title { font-size: 9px; }
  #hud-phase, #hud-score { font-size: 8px; }
  .phase-arrow { font-size: 14px; padding: 6px 10px; }
  .theme-grid { grid-template-columns: repeat(2, 1fr); }
  .color-grid { grid-template-columns: repeat(4, 1fr); }
  .setup-card { padding: 16px; }
}

@media (min-width: 601px) and (max-width: 1100px) {
  .chr-svg { width: 80px; height: 120px; }
  #stage { gap: 12px; }
}

