// ============================================================
// SPRUNKS — Página do Jogo
// Rota: /sprunks
// ============================================================

import SprunksGame from '@/components/sprunki/SprunksGame'
import '@/styles/sprunks.css'

export const metadata = {
  title: 'SPRUNKS! — Sequenciador Musical',
  description: 'Jogo musical com personagens customizáveis e fases surpresa',
}

export default function SprunksPage() {
  return <SprunksGame />
}

// ============================================================
// SPRUNKS — API Route: Gerar Personagem por Prompt
//
// Endpoint: POST /api/generate-character
//
// Recebe o relato dos pais e retorna personagens gerados.
// Pode ser usado com IA generativa no futuro (Claude, etc.)
// para gerar SVGs ainda mais customizados.
//
// Por enquanto usa o motor local (character-generator.ts).
// ============================================================

import { NextRequest, NextResponse } from 'next/server'
import type { ChildPreference } from '@/lib/sprunki/types'
import { generateSurpriseCharacters } from '@/lib/sprunki/character-generator'

export async function POST(request: NextRequest) {
  try {
    const body = await request.json() as ChildPreference

    // Validação mínima
    if (!body.favoriteTheme && !body.parentDescription) {
      return NextResponse.json(
        { error: 'Precisa de pelo menos um tema ou descrição' },
        { status: 400 }
      )
    }

    // Gera personagens baseado na preferência
    const characters = generateSurpriseCharacters({
      childName: body.childName || 'Jogador',
      favoriteTheme: body.favoriteTheme || '',
      favoriteColors: body.favoriteColors || [],
      parentDescription: body.parentDescription || '',
    })

    // Retorna os personagens (sem as funções — serializa só os dados)
    const serialized = characters.map(c => ({
      name: c.name,
      color: c.color,
      type: c.type,
      description: c.description,
      pattern: c.pattern,
      notes: c.notes,
      isSurprise: c.isSurprise,
      theme: c.theme,
    }))

    return NextResponse.json({
      success: true,
      characters: serialized,
      theme: characters[0]?.theme || 'custom',
    })
  } catch (error) {
    return NextResponse.json(
      { error: 'Erro ao gerar personagens' },
      { status: 500 }
    )
  }
}

// ============================================================
// SPRUNKS — Export central
// Importa tudo de um lugar só: import { ... } from '@/lib/sprunki'
// ============================================================

// Tipos
export type {
  Character,
  Phase,
  ChildPreference,
  GeneratedCharacter,
  SoundType,
  SynthFunction,
  SVGGenerator,
  SequencerState,
  GameState,
} from './types'

// Notas e constantes
export { NOTES, BPM, STEP_DURATION, TOTAL_STEPS } from './notes'

// Áudio
export { initAudio, destroyAudio, setMasterVolume, getAudioContext, getMasterGain } from './audio-context'

// Síntese
export {
  kick, snare, hihat, shaker, elec, lead, vibes, pad, sub,
  vocal, darkVocal, darkVocal2, glitch, whistle, robot, sonicKick, powerHit,
} from './synths'

// SVG
export { sprunkiSVG, customSVG } from './svg-builders'

// Personagens
export { ALL_PHASES, getPhaseCharacters } from './characters'

// Fases
export { BASE_PHASES, generateSurprisePhase } from './phases'

// Gerador de personagens
export { generateSurpriseCharacters, getAvailableThemes, isKnownTheme } from './character-generator'

// Motor de surpresas
export { buildGameSequence, buildDefaultSequence, calculateTotalPhases } from './surprise-engine'
export type { GamePhase } from './surprise-engine'

// Sequenciador
export {
  connectSequencer, startSequencer, stopSequencer,
  startIntegratedSequencer, resetSequencer,
  isSequencerPlaying, getCurrentStep, onStep,
} from './sequencer'

