  if (!freq) return
  const o = x.createOscillator(), g = x.createGain()
  o.type = 'sine'; o.frequency.value = freq
  g.gain.setValueAtTime(0.2, t); g.gain.exponentialRampToValueAtTime(0.001, t + ST * 4)
