  const o = x.createOscillator(), g = x.createGain(), fl = x.createBiquadFilter()
  if (tp === 'high') { o.type = 'triangle'; fl.type = 'bandpass'; fl.frequency.value = freq; fl.Q.value = 3; g.gain.setValueAtTime(0.13, t) }
