  const fl = x.createBiquadFilter(); fl.type = 'lowpass'; fl.frequency.value = 200
  o1.connect(fl); o2.connect(fl); fl.connect(g); g.connect(d)
  o1.start(t); o1.stop(t + dur + 0.1); o2.start(t); o2.stop(t + dur + 0.1)
}

