input.content:
@import url('https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap');
*{margin:0;padding:0;box-sizing:border-box}
body{font-family:'Press Start 2P',cursive;background:#000;color:#fff;min-height:100vh;overflow:hidden;user-select:none;image-rendering:pixelated}

#scene{position:fixed;top:0;left:0;width:100%;height:100%;overflow:hidden;transition:filter 2s ease}
#sky{position:absolute;top:0;left:0;width:100%;height:55%;transition:background 2s ease}
.star{position:absolute;background:#fff;border-radius:50%;opacity:0;transition:opacity 2s ease}
#mountains{position:absolute;bottom:42%;left:0;width:200%;height:20%;animation:px-slow 30s linear infinite}
.mountain{position:absolute;bottom:0;width:0;height:0;border-left:80px solid transparent;border-right:80px solid transparent;border-bottom:100px solid #304878;opacity:.6}
#hills{position:absolute;bottom:30%;left:0;width:200%;height:20%;animation:px-mid 20s linear infinite}
.hill{position:absolute;bottom:0;width:120px;height:60px;background:#206030;border-radius:60px 60px 0 0}
@keyframes px-slow{0%{transform:translateX(0)}100%{transform:translateX(-50%)}}
@keyframes px-mid{0%{transform:translateX(0)}100%{transform:translateX(-50%)}}
#ground{position:absolute;bottom:0;left:0;width:100%;height:32%;overflow:hidden}
#grass-line{position:absolute;top:0;left:0;width:100%;height:8px;background:#40C040;z-index:2}
#checkerboard{position:absolute;top:8px;left:0;width:100%;height:100%;background-size:40px 40px;transform:perspective(400px) rotateX(15deg);transform-origin:top center}

#game-ui{position:relative;z-index:10;width:100%;height:100vh;display:flex;flex-direction:column;align-items:center;justify-content:flex-start;padding-top:12px}
#hud{display:flex;justify-content:space-between;align-items:center;width:90%;max-width:800px;padding:8px 16px;background:rgba(0,0,0,.7);border:2px solid #C9A84C;border-radius:4px;margin-bottom:8px}
#hud-phase,#hud-score,#hud-title{color:#C9A84C}
#hud-phase,#hud-score{font-size:10px}
#hud-title{font-size:12px;color:#fff}

#step-row{display:flex;gap:3px;margin-bottom:6px;height:6px}
.sdot{width:6px;height:6px;border-radius:50%;background:rgba(255,255,255,.1);transition:background .05s}
.sdot.on{background:#C9A84C}

#viz{display:flex;gap:2px;height:28px;align-items:flex-end;margin-bottom:6px}
.vbar{width:3px;background:rgba(201,168,76,.15);border-radius:1px;min-height:2px;transition:height .08s}

#stage{display:flex;justify-content:center;align-items:flex-end;gap:8px;margin-bottom:8px;min-height:200px;flex-wrap:wrap;padding:0 10px}
.chr{display:flex;flex-direction:column;align-items:center;cursor:pointer;transition:all .2s;-webkit-tap-highlight-color:transparent;touch-action:manipulation}
.chr:active{transform:scale(.95)}
.chr-svg{width:72px;height:110px;transition:filter .3s,transform .3s}
.chr.off .chr-svg{filter:grayscale(1) brightness(.4)}
.chr.on .chr-svg{filter:drop-shadow(0 0 8px var(--c));transform:translateY(-6px)}
.chr.on .chr-note{opacity:1}
.chr-name{font-size:6px;margin-top:2px;color:#888;transition:color .3s}
.chr.on .chr-name{color:#fff}
.chr-type{font-size:5px;color:#555;margin-top:1px;padding:1px 4px;background:rgba(255,255,255,.05);border-radius:2px}
.chr-note{position:absolute;top:-4px;right:2px;font-size:14px;opacity:0;animation:note-bob 1.5s ease-in-out infinite;pointer-events:none}
@keyframes note-bob{0%,100%{transform:translateY(0)}50%{transform:translateY(-8px)}}

#phase-nav{display:flex;align-items:center;gap:16px;margin-bottom:8px}
.phase-arrow{font-size:18px;color:#C9A84C;cursor:pointer;padding:8px 14px;border:2px solid #C9A84C;border-radius:4px;background:rgba(0,0,0,.5);transition:all .15s;-webkit-tap-highlight-color:transparent;font-family:'Press Start 2P',cursive}
.phase-arrow:active{background:#C9A84C;color:#000}
.phase-arrow.disabled,.phase-arrow:disabled{opacity:.2;pointer-events:none}
#phase-label{font-size:9px;color:#C9A84C;min-width:100px;text-align:center}

#prog-wrap{width:80%;max-width:400px;background:rgba(0,0,0,.5);border:1px solid #333;border-radius:3px;height:8px;overflow:hidden;margin-bottom:6px}
#prog-bar{height:100%;width:0%;border-radius:3px;background:linear-gradient(90deg,#C9A84C,#F7C948);transition:width .4s}
#prog-text{font-size:7px;color:#666;margin-bottom:4px}

#overlay{position:fixed;top:0;left:0;width:100%;height:100%;background:#000;opacity:0;pointer-events:none;transition:opacity .6s;z-index:100;display:flex;align-items:center;justify-content:center;flex-direction:column}
#overlay.show{opacity:1;pointer-events:all}
#overlay-text{font-size:16px;color:#C9A84C;margin-bottom:8px}
#overlay-sub{font-size:8px;color:#666}

#win{display:none;position:fixed;top:0;left:0;width:100%;height:100%;background:radial-gradient(circle,#1a1a2e,#000);z-index:200;flex-direction:column;align-items:center;justify-content:center}
#win.show{display:flex}
#win h1{font-size:18px;color:#C9A84C;margin-bottom:12px}
#win p{font-size:8px;color:#666;margin-bottom:16px}
#win button{font-family:'Press Start 2P',cursive;font-size:9px;padding:10px 20px;background:transparent;border:2px solid #C9A84C;color:#C9A84C;border-radius:4px;cursor:pointer;transition:all .15s}
#win button:hover{background:#C9A84C;color:#000}

.ptcl{position:fixed;border-radius:50%;pointer-events:none;animation:ptcl-up 1.5s ease-out forwards;z-index:50}
@keyframes ptcl-up{0%{opacity:1;transform:translateY(0) scale(1)}100%{opacity:0;transform:translateY(-60px) scale(0)}}

.parent-setup{position:fixed;top:0;left:0;width:100%;height:100%;background:radial-gradient(circle at 50% 30%,#1a1a3e,#000);display:flex;align-items:center;justify-content:center;z-index:300;padding:20px}
.setup-card{background:rgba(20,20,40,.9);border:2px solid #C9A84C;border-radius:8px;padding:24px;max-width:440px;width:100%}
.setup-title{font-size:14px;color:#C9A84C;margin-bottom:8px;text-align:center}
.setup-desc{font-size:7px;color:#888;margin-bottom:16px;text-align:center;line-height:1.6}
.setup-label{display:block;font-size:7px;color:#C9A84C;margin-bottom:6px}
.setup-input{width:100%;background:rgba(0,0,0,.5);border:1px solid #444;border-radius:4px;padding:8px 12px;color:#fff;font-family:'Press Start 2P',cursive;font-size:8px;margin-bottom:16px;outline:none;transition:border-color .2s}
.setup-input:focus{border-color:#C9A84C}
.setup-input::placeholder{color:#555}
.theme-grid{display:grid;grid-template-columns:repeat(2,1fr);gap:6px;margin-bottom:8px}
.theme-btn{font-family:'Press Start 2P',cursive;font-size:7px;padding:8px 6px;background:rgba(0,0,0,.5);border:1px solid #333;border-radius:4px;color:#888;cursor:pointer;transition:all .15s}
.theme-btn:hover{border-color:#666;color:#aaa}
.theme-btn.selected{border-color:#C9A84C;color:#C9A84C;background:rgba(201,168,76,.1)}
.color-grid{display:grid;grid-template-columns:repeat(4,1fr);gap:6px;margin-bottom:16px}
.color-btn{display:flex;flex-direction:column;align-items:center;gap:4px;padding:6px;background:rgba(0,0,0,.5);border:1px solid #333;border-radius:4px;cursor:pointer;transition:all .15s;font-family:'Press Start 2P',cursive;font-size:5px;color:#666}
.color-btn:hover{border-color:#666}
.color-btn.selected{border-color:var(--btn-color);color:#fff;background:rgba(255,255,255,.05)}
.color-swatch{width:24px;height:24px;border-radius:50%;border:2px solid transparent;transition:border-color .15s}
.color-btn.selected .color-swatch{border-color:#fff}
.setup-actions{display:flex;gap:8px;justify-content:center;margin-top:16px}
.setup-btn{font-family:'Press Start 2P',cursive;font-size:8px;padding:10px 16px;border-radius:4px;cursor:pointer;transition:all .15s}
.setup-btn.primary{background:#C9A84C;color:#000;border:2px solid #C9A84C}
.setup-btn.primary:hover{background:#F7C948}
.setup-btn.secondary{background:transparent;color:#666;border:2px solid #333}
.setup-btn.secondary:hover{border-color:#666;color:#999}

@media(max-width:600px){.chr-svg{width:54px;height:82px}#stage{gap:4px;min-height:140px}.chr-name{font-size:5px}#hud{padding:6px 10px}#hud-title{font-size:9px}#hud-phase,#hud-score{font-size:8px}.phase-arrow{font-size:14px;padding:6px 10px}}
@media(min-width:601px) and (max-width:1100px){.chr-svg{width:80px;height:120px}#stage{gap:12px}}

